import React, { Component } from "react";
import { Link } from "react-router-dom";
import firebase from 'firebase/compat/app'; 
import 'firebase/compat/auth'; 
import 'firebase/compat/firestore';

class Cadastro extends Component {
  constructor(props) {
    super(props);

    this.state = {
      formData: {
        email: "",
        password: "",
        nome: "",
        sobrenome: "",
        dataNascimento:""
      },
      validationEmail: false,
      validationPassword: false,
      validationNome: false,
      validationSobrenome: false,
      validationDataNascimento: false,
      showStatus: false
    };
    this.gravar = this.gravar.bind(this)
  }
   gravar(){
    firebase.auth().createUserWithEmailAndPassword(this.state.formData.email, this.state.formData.password).then((userCredential)=>{
      const user = userCredential.user
    }).catch((error));
  
    firebase.firestore().collection('usuario').add(
      {
        email: this.state.formData.email,
        password: this.state.formData.password,
        nome: this.state.formData.nome,
        sobrenome: this.state.formData.sobrenome,
        dataNascimento: this.state.formData.dataNascimento
      }
   )
  }

  handleInputChange(fieldName, value) {
    this.setState((prevState) => ({
      formData: {
        ...prevState.formData,
        [fieldName]: value,
      },
    }));
  }

  handleSubmit(e) {
    e.preventDefault();
this.gravar();
    console.log(this.state.formData);
  }

  render() {
    return (
      <form onSubmit={(e) => this.handleSubmit(e)}>
        <h1>Cadastre-se</h1>
        <div className="login">
         <label htmlFor="email">E-mail</label>
          <input
          id="email"
          placeholder="Informe um e-mail"
            type="email"
            value={this.state.formData.email || ""}
            onChange={(e) => this.handleInputChange("email", e.target.value)}
          />
                <label htmlFor="senha">Senha</label>
          <input
          id="senha"
          placeholder="Digite a senha desejada"
            type="password"
            value={this.state.formData.password || ""}
            onChange={(e) => this.handleInputChange("password", e.target.value)}
          />
           <label htmlFor="nome">Nome</label>
          <input
          id="nome"
          placeholder="Digite o seu primeiro nome"
            type="text"
            value={this.state.formData.nome || ""}
            onChange={(e) => this.handleInputChange("nome", e.target.value)}
          />
           <label htmlFor="sobrenome">Sobrenome</label>
          <input
          id="sobrenome"
          placeholder="Digite o seu sobrenome"
            type="text"
            value={this.state.formData.sobrenome || ""}
            onChange={(e) => this.handleInputChange("sobrenome", e.target.value)}
          />
           <label htmlFor="dataNascimento">Data de Nascimento</label>
          <input
          id="dataNascimento"
            type="date"
            value={this.state.formData.dataNascimento || ""}
            onChange={(e) => this.handleInputChange("dataNascimento", e.target.value)}
          />
        </div>
        <button type="submit">Cadastrar usuário</button>
        <Link to="/">Login</Link>
        <div className={this.state.showStatus === true ? "show" : "hide"}>
          {this.state.validationEmail === true &&
          this.state.validationPassword === true ? (
            <p>Acessado com sucesso!</p>
          ) : (
            <p>Usuário ou senha incorretos</p>
          )}
        </div>
      </form>
    );
  }
}

export default Cadastro;
