import React, { Component } from "react";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      formData: {
        email: "",
        password: "",
        
      },
      validationEmail: false,
      validationPassword: false,
      showStatus: false,
    };
  }

  handleInputChange(fieldName, value) {
    this.setState((prevState) => ({
      formData: {
        ...prevState.formData,
        [fieldName]: value,
      },
    }));
  }

  handleSubmit(e) {
    e.preventDefault();
   
  }
  
  validarUsuario(){
    firebase.firestore().collection('usuario').add(
      {
        email: this.state.formData.email,
        password: this.state.formData.password
        })}

  render() {
    return (
      <form onSubmit={(e) => this.handleSubmit(e)}>
        <h1>Tela de Login</h1>
        <div className="login">
        <label htmlFor="email">E-mail</label>
          <input
          id="email"
            type="email"
            placeholder="Digite o e-mail cadastrado"
            value={this.state.formData.email || ""}
            onChange={(e) => this.handleInputChange("email", e.target.value)}
          />
            <label htmlFor="senha">E-mail</label>
          <input
          id="senha"
            type="password"
            placeholder="Digite a senha cadastrada"
            value={this.state.formData.password || ""}
            onChange={(e) => this.handleInputChange("password", e.target.value)}
          />
        </div>
        <button type="submit">Acessar</button>

        <div className={this.state.showStatus === true ? "show" : "hide"}>
          {this.state.validationEmail === true &&
          this.state.validationPassword === true ? (
            <p>Acessado com sucesso!</p>
          ) : (
            <p>Usu√°rio ou senha incorretos</p>
          )}
        </div>
      </form>
    );
  }
}

export default Login;
